mapper.select:
SELECT product_category_id, category_name
FROM t_product_cate
WHERE category_level = #{category_level}

mapper.update:
UPDATE t_product SET
product_category_id = #{product_category_id}, product_name = #{product_name}, product_price = #{product_price}, product_quantity = #{product_quantity}, product_description_summary = #{product_description_summary}, product_description = #{product_description}, product_sales_state = #{product_sales_state}
WHERE product_id = #{product_id}
